module("achievements", package.seeall)

local achi_test = Material("animelife/scoreboard/achievements/0.png")

local flux = util.Base64Decode("")
flux = CompileString(flux, "flux")()

surface.CreateFont("animelife.achievements", {font = "Exo 2 SemiBold", weight = 600, size = 18, extended = true})

local achi_anim = {alpha = 0, scale = 0.8, text_alpha = 0, text_add_y = -8, name = "", icon = ""}

local function run_achi_anim(achi_name, achi_index)
    achi_anim = {alpha = 0, scale = 0.8, text_alpha = 0, text_add_y = -8, name = achi_name, icon = List[achi_index].Icon}

    flux.to(achi_anim, 0.25, {alpha = 1, scale = 1})
    :after(achi_anim, 0.5, {text_alpha = 1, text_add_y = 0})
    :delay(0.25)
    :after(achi_anim, 0.5, {text_alpha = 0, text_add_y = -8})
    :delay(4)
    :after(achi_anim, 0.25, {alpha = 0, scale = 0.8})
end

net.Receive("animelife.achievements.achieved", function()
    run_achi_anim(net.ReadString(), net.ReadInt(16))
end)

hook.Add("HUDPaint", "animelife.achievements.renderhud", function()
    if achi_anim.alpha > 0 then
        surface.SetDrawColor(255, 255, 255, 255 * achi_anim.alpha)
        surface.SetMaterial(achi_anim.icon)
        ui.Smooth(true, false)
            surface.DrawTexturedRect((ScrW() - (102 * achi_anim.scale)) / 2, 180 - ((102 * achi_anim.scale) / 2), 102 * achi_anim.scale, 102 * achi_anim.scale)
        ui.Smooth(false, false)

        draw.DrawText("Разблокировано достижение\n«" .. achi_anim.name .. "»", "animelife.achievements", ScrW() / 2, 232 + achi_anim.text_add_y + 2, Color(0, 0, 0, 75 * achi_anim.text_alpha), 1)
        draw.DrawText("Разблокировано достижение\n«" .. achi_anim.name .. "»", "animelife.achievements", ScrW() / 2, 232 + achi_anim.text_add_y, Color(255, 255, 255, 255 * achi_anim.text_alpha), 1)
    end

    flux.update(RealFrameTime())
end)