local bright_preset = {
    ["$pp_colour_addr"] = 0.03,
	["$pp_colour_addg"] = 0.02,
	["$pp_colour_addb"] = 0.03,
	["$pp_colour_brightness"] = 0.03,
	["$pp_colour_contrast"] = 1.07,
	["$pp_colour_colour"] = 1,
	["$pp_colour_mulr"] = 0,
	["$pp_colour_mulg"] = 0,
	["$pp_colour_mulb"] = 0
}

local dark_preset = {
    ["$pp_colour_addr"] = 0,
	["$pp_colour_addg"] = 0,
	["$pp_colour_addb"] = 0,
	["$pp_colour_brightness"] = -0.15,
	["$pp_colour_contrast"] = 1,
	["$pp_colour_colour"] = 1,
	["$pp_colour_mulr"] = 0,
	["$pp_colour_mulg"] = 0,
	["$pp_colour_mulb"] = 0
}

GLOBALS_POSTPROCESSING = {
	["bright"] = {
		Name = "яркий",
		Appearance = function()
			DrawColorModify(bright_preset)
			DrawBloom(0.5, 1.2, 10, 10, 1, 1, 1, 0.9, 1)
		end
	},
	["bloom"] = {
		Name = "умножение света",
		Appearance = function()
			DrawBloom(0.6, 1.1, 25, 25, 1, 1, 1, 1, 1)
		end
	},
	["dark"] = {
		Name = "темный",
		Appearance = function()
			DrawColorModify(dark_preset)
		end
	},
}

hook.Add("RenderScreenspaceEffects", "animelife.settings.postprocessing", function()
    local preset = cookie.GetString("animelife.settings.pp_preset", "none")
    if GLOBALS_POSTPROCESSING[preset] then
		GLOBALS_POSTPROCESSING[preset].Appearance()
    end
end)

local icon_sunglasses = Material("animelife/postprocessing/icon_sunglasses.png")
local preset_anim = {alpha = 0, add_y = 0, icon_add_y = 0, next_update = 0}

surface.CreateFont("animelife.postprocessing.top", {font = "Exo 2 Medium", weight = 500, size = 18, extended = true})
surface.CreateFont("animelife.postprocessing.bottom", {font = "Exo 2 SemiBold", weight = 600, size = 20, extended = true})

local flux = util.Base64Decode("")
flux = CompileString(flux, "flux")()

local function run_preset_anim()
	flux.to(preset_anim, 0.5, {alpha = 0.94, add_y = 16})
	:after(preset_anim, 0.5, {alpha = 0, add_y = 0})
	:delay(4)
end

hook.Add("OnPostProcessingPresetChanged", "animelife.settings.postprocessing", function()
	run_preset_anim()
end)

hook.Add("HUDPaint", "animelife.settings.postprocessing", function()
	local preset = cookie.GetString("animelife.settings.pp_preset", "none")
	if preset == "none" then return end

	if preset_anim.alpha > 0 then
		draw.RoundedBox(8, (ScrW() - 308) / 2, 94 + preset_anim.add_y, 308, 63, Color(47, 47, 47, 255 * preset_anim.alpha))
		draw.RoundedBox(8, (ScrW() - 308) / 2, 104 + preset_anim.add_y, 4, 42, Color(255, 223, 142, 255 * preset_anim.alpha))

		surface.SetDrawColor(255, 255, 255, 255 * preset_anim.alpha)
		surface.SetMaterial(icon_sunglasses)
		surface.DrawTexturedRect((ScrW() - 57) / 2, 63 + preset_anim.icon_add_y + preset_anim.add_y, 57, 49)

		draw.SimpleText("Установлен пресет постобработки", "animelife.postprocessing.top", ScrW() / 2, 106 + preset_anim.add_y, Color(255, 255, 255, 255 * preset_anim.alpha), 1)
		draw.SimpleText(GLOBALS_POSTPROCESSING[preset].Name, "animelife.postprocessing.bottom", ScrW() / 2, 106 + 18 + preset_anim.add_y, Color(255, 255, 255, 255 * preset_anim.alpha), 1)

		if preset_anim.next_update < CurTime() then
			flux.to(preset_anim, 0.5, {icon_add_y = -16})
			:after(preset_anim, 0.5, {icon_add_y = 0})

			preset_anim.next_update = CurTime() + 0.7
		end
	end

	if system.HasFocus() then
		flux.update(RealFrameTime())
	end
end)