local PANEL = {}

local background = Material("animelife/bgstore/background.png")

local flux = util.Base64Decode("")
flux = CompileString(flux, "flux")()

FONT_WEIGHT_BOLD = 700
FONT_WEIGHT_REGULAR = 400
FONT_WEIGHT_LIGHT = 300
FONT_WEIGHT_MEDIUM = 500
FONT_WEIGHT_SEMIBOLD = 600

surface.CreateFont("animelife.bgstore.category", {font = "Exo 2", size = 29, weight = FONT_WEIGHT_BOLD, extended = true})
surface.CreateFont("animelife.bgstore.category.shadow", {font = "Exo 2", size = 29, weight = FONT_WEIGHT_BOLD, blursize = 4, extended = true})
surface.CreateFont("animelife.bgstore.category.button", {font = "Exo 2 SemiBold", size = 17, weight = FONT_WEIGHT_SEMIBOLD, extended = true})
surface.CreateFont("animelife.bgstore.item", {font = "Exo 2", size = 17, weight = FONT_WEIGHT_BOLD, extended = true})
surface.CreateFont("animelife.bgstore.price", {font = "Exo 2", size = 43, weight = FONT_WEIGHT_BOLD, extended = true})

function PANEL:Init()
    self:SetAlpha(0)
    self:AlphaTo(255, 0.25)

    self.ScrollList = vgui.Create("DScrollPanel", self)
    self.ScrollList:SetPos(423, 0)
    self.ScrollList:SetSize(ScrW() - 423 - 140, ScrH())
    local v_bar = self.ScrollList:GetVBar()
    v_bar:SetWide(6)
    v_bar.Paint = function() end
    v_bar.btnUp.Paint = function() end
    v_bar.btnDown.Paint = function() end
    v_bar.btnGrip.Paint = function(panel, w, h)
        draw.RoundedBox(6, 0, 0, w, h, Color(250, 103, 120, 45))
    end

    self.CloseButton = vgui.Create("DButton", self)
    self.CloseButton:SetPos(ScrW() - 24 - 31, 26)
    self.CloseButton:SetSize(24, 24)
    self.CloseButton:SetText("")
    self.CloseButton.Paint = function() end
    self.CloseButton.DoClick = function()
        if ValidPanel(self) then
            self:Remove()
            self = nil
        end
    end

    local x, y = 0, 170
    local i = 1
    local by = 168
    for key, cat in pairs(playerstats.BackgroundStore) do
        for j = 1, 2 do
            local category_text = vgui.Create("DLabel", self.ScrollList)
            category_text:SetPos(x + 102, y - 69)
            category_text:SetText("| Фоны категории " .. key)
            category_text:SetFont(j == 2 and "animelife.bgstore.category" or "animelife.bgstore.category.shadow")
            category_text:SetColor(j == 2 and Color(255, 255, 255) or Color(0, 0, 0, 45))
            category_text:SizeToContents()

            cat.ScrollChild = category_text
        end

        local side_button = vgui.Create("DButton", self)
        side_button:SetPos(32, by)
        side_button:SetSize(206, 40)
        side_button:SetText("")
        side_button.Paint = function(panel, w, h)
            draw.RoundedBox(6, 0, 0, w, h, Color(250, 103, 120))

            draw.SimpleText(key, "animelife.bgstore.category.button", w / 2, h / 2, Color(255, 255, 255), 1, 1)
        end
        side_button.DoClick = function()
            self.ScrollList:ScrollToChild(cat.ScrollChild)
        end

        by = by + 40 + 16

        for index, item in pairs(cat) do
            if item.Hidden then continue end
            if ispanel(item) then continue end
            local item_panel = vgui.Create("DPanel", self.ScrollList)
            item_panel:SetPos(x, y)
            item_panel:SetSize(402, 176)
            item_panel:SetCursor("hand")
            item_panel.Button = nil
            item_panel.Parameters = {alpha = 0, price_x = 16, button_y = 5}
            item_panel.Paint = function(panel, w, h)
                surface.SetDrawColor(255, 255, 255)
                surface.SetMaterial(item.Material)
                surface.DrawTexturedRect(0, 0, w, h)

                draw.SimpleText(item.Name, "animelife.bgstore.item", 35, 20, Color(255, 255, 255, 95))

                draw.SimpleText("¥" .. string.Comma(item.Price or 0), "animelife.bgstore.price", panel.Parameters.price_x, h - 26, Color(255, 255, 255, 150 * panel.Parameters.alpha), nil, 4)

                draw.RoundedBox(6, w - 114 - 37, h - 40 - 23 + panel.Parameters.button_y, 114, 40, Color(250, 103, 120, 255 * panel.Parameters.alpha))
                draw.SimpleText(LocalPlayer():HasBackground(index) and "Установить" or "Купить", "animelife.bgstore.category.button", w - (114 / 2) - 37, h - 20 - 23 + panel.Parameters.button_y, Color(255, 255, 255, 255 * panel.Parameters.alpha), 1, 1 )
            end
            item_panel.OnCursorEntered = function(panel)
                flux.to(panel.Parameters, 0.8, {alpha = 1, price_x = 35, button_y = 0})
                
                // if ValidPanel(panel.Button) then
                //     panel.Button:SetVisible(true)
                // end
            end
            item_panel.OnCursorExited = function(panel)
                if panel.Button:IsHovered() then return end
                flux.to(panel.Parameters, 0.8, {alpha = 0, price_x = 16, button_y = 5})

                // if ValidPanel(panel.Button) then
                //     panel.Button:SetVisible(false)
                // end
            end

            item_panel.Button = vgui.Create("DButton", item_panel)
            item_panel.Button:SetPos(item_panel:GetWide() - 114 - 37, item_panel:GetTall() - 40 - 23)
            item_panel.Button:SetSize(114, 40)
            item_panel.Button:SetText("")
            item_panel.Button.Paint = function() end
            // item_panel.Button:SetVisible(false)
            item_panel.Button.DoClick = function()
                net.Start("animelife.playerstats.background_buy")
                    net.WriteInt(index, 16)
                net.SendToServer()

                surface.PlaySound("click.wav")
            end

            if i % 3 == 0 then
                x = 0
                i = 1
                y = y + 176 + 36
            else
                x = x + 402 + 50
                i = i + 1
            end
        end

        if i % 3 == 0 then
            x = 0
            i = 1
            y = y + 176 + 128
        else
            y = y + 86
        end
    end
end

function PANEL:Paint(w, h)
    surface.SetDrawColor(255, 255, 255)
    surface.SetMaterial(background)
    surface.DrawTexturedRect(0, 0, w, h)

    flux.update(RealFrameTime())
end

vgui.Register("animelife.bgstore", PANEL, "EditablePanel")

function OpenBackgroundStore()
    local s = vgui.Create("animelife.bgstore")
    s:SetSize(ScrW(), ScrH())
    s:MakePopup()
end